import React, { useState, useEffect } from 'react';
import { MenuItem, FormControl, Select, Card} from "@material-ui/core";
import InfoBox from './InfoBox';
import Map from "./Map";
import './App.css';

function App() {
  const [countries, setCountries] = useState([]);
  //default select item
  const [country, setCountry] = useState('worldwide');

// STATE = How to write a variable in REACT
//
//USEEFFECT = Runs a piece of code base on a given condition

useEffect(() => {
  //async  -> send a request, wait for it, do something with the info

  const getCountriesData = async () => {
    await fetch ("https://disease.sh/v3/covid-19/countries")
    .then((response) => response.json()) //get response and take the json from it
    .then((data) => {
      const countries = data.map((country) => ({
          name: country.country, //United States, United Kingdom
          value: country.countryInfo.iso2 //UK, USA, FR
        }));

      setCountries(countries);

    });
  };

  getCountriesData();
}, []);  //anytime [countries] is loaded the code in the useEffect is run

const onCountryChange = (event) => {
  const countryCode = event.target.value;

  console.log('YOOOO >>>', countryCode);
  setCountry(countryCode);
};

  return (
    <div className="App">

   <div className="app__left">
   <div className="app__header">
        <h1>COVID-19 tracker</h1>
        <FormControl className="app__dropdown">
          <Select variant="outlined" onChange={onCountryChange} value={country}>
            <MenuItem value="worldwide">Worldwide</MenuItem>

            {
              countries.map((country) => (
                <MenuItem value={country.value}>{country.name}</MenuItem>
              ))
            }


          </Select>
        </FormControl>
      </div>

    <div className="app__stats">
      <InfoBox title="Coronavirus Cases" cases={123} total={2000} />

      <InfoBox title="Recovered" cases={1234} total={3000}/>

      <InfoBox title="Deaths" cases={12345} total={4000}/>
    </div>

      {/* Table */}
      {/* Graph */}

      {/* Map */}
      <Map />
   </div>

      {/*Table */}
      {/*Graph */}
      <Card className="">
        <CardContent>
          <h3>Live Cases by Contry</h3>
        </CardContent>
      </Card>
    </div>
  );
}

export default App;
